import React from 'react';
import Article from '../../components/article/Article';
import { BoardLayout, Dynamics365NGO, MicrosoftDynamicsNAV, onedrive, dynamics365 } from './imports';
import './blog.css';

const Blog = () => (
  <div className="deft__blog section__padding" id="blog">
    <div className="deft__blog-heading">
      <h1 className="gradient__text">Drive business digitisation, <br /> with the right tools for your industry challenges.</h1>
    </div>
    <div className="deft__blog-container">
      <div className="deft__blog-container_groupA">
        <Article imgUrl={MicrosoftDynamicsNAV} date="Sep 26, 2021" text="Deft E-Board" />
      </div>
      <div className="deft__blog-container_groupB">
        <Article imgUrl={Dynamics365NGO} date="Sep 26, 2021" text="MICROSOFT DYNAMICS CRM (NON-PROFIT,MICRO-FINANCE)" />
        <Article imgUrl={MicrosoftDynamicsNAV} date="Sep 26, 2021" text="MICROSOFT DYNAMICS 365 (F&O, AX, NAV, RETAIL)" />
        <Article imgUrl={onedrive} date="Sep 26, 2021" text="MICROSOFT OFFICE 365 (WORD, TEAMS, ONEDRIVE)" />
        <Article imgUrl={dynamics365} date="Sep 26, 2021" text="MICROSOFT DYNAMICS 365 BUSINESS CENTRAL" />
      </div>
    </div>
  </div>
);

export default Blog;
