import React from 'react';
import Feature from '../../components/feature/Feature';
import './features.css';

const featuresData = [
  {
    title: 'Sales',
    text: 'Standard Microsoft Dynamics 365 solutions for sales departments and the sales force with the ability to integrate/engage reps and distributors. The solution allows you to quickly identify business opportunities, establish stable contact with customers or leads and prioritise and organise the sales staffs agendas quickly. and prioritise and organise sales peoples agendas quickly.',
  },
  {
    title: 'Finances',
    text: 'Finance and administration departments need reliable and fast tools to securely optimise day-to-day business transactions. With Microsoft Dynamics solutions (NAVISION and BUSINESS CENTRAL), in addition to the annexed solutions that enrich the platform for financial departments such as the digitalisation of documents, the management of the SII, IRPF, etc.,',
  },
  {
    title: 'Digital Marketing',
    text: 'As a marketer you need an analytical marketing tool that allows you to centralise all the data from different sources (customer interactions, social media, events...) and draw the necessary conclusions to set realistic targets . In addition, Microsoft has tools that automate marketing processes and it is possible to design the best strategy for marketing campaigns through various methods, including surveys, interactive strategies that are defined by the most successful results, etc.',
  },
  {
    title: 'Operations/ Project Management',
    text: 'Microsoft provides tools that make it easier to meet that budget and timeline so that operations professionals can focus on the actual project development. Microsoft solutions optimise all processes through process automation and optimal resource management planning. In addition, these solutions centralise all information and facilitate reliable analytics and insights.'
  },
  {
    title
  }
];

const Features = () => (
  <div className="gpt3__features section__padding" id="features">
    <div className="gpt3__features-heading">
      <h1 className="gradient__text">The Future is Now and You Just Need to Realize It. Step into Future Today. & Make it Happen.</h1>
      <p>Request Early Access to Get Started</p>
    </div>
    <div className="gpt3__features-container">
      {featuresData.map((item, index) => (
        <Feature title={item.title} text={item.text} key={item.title + index} />
      ))}
    </div>
  </div>
);

export default Features;
